import config from '../../config'
import React, { Component } from 'react'
import { Table } from "reactstrap";
import axios from 'axios'
import { Link } from 'react-router-dom'
import "./ViewCustomers.css";
import "bootstrap/dist/css/bootstrap.css";


export class ViewCustomer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      cust_data: [],
    };
  }

  componentDidMount() {
    //get request
    axios.get(`${config.webapi}/get_cust_data.php`).then(
      (res) => {
        this.setState({ cust_data: res.data });
      },
      (error) => {
        console.log(error);
      }
    );
  }
  render() {
    return (
      <div className="container">
        <h2 className="text-center my-3">All Customer List</h2>
        <hr />
        <div className="row">
          <div className="col-md-8"></div>
          <div className="col-md-4">
            <Button variant="warning" onClick={this.transferMoney}>
              Tranfer Money
            </Button>
          </div>
        </div>
        <Table responsive striped className="container my-5">
          <thead>
            <tr>
              <th>#id</th>
              <th>Customer Name</th>
              <th>Customer Email</th>
              <th>Current Balance</th>
              <th>View</th>
            </tr>
          </thead>
          <tbody>
            {this.state.cust_data.map((cust) => {
              return (
                <tr key={cust.cust_id}>
                  <td>{cust.cust_id}</td>
                  <td>{cust.cust_name}</td>
                  <td>{cust.cust_email}</td>
                  <td>Rs. {cust.cust_curr_bal}</td>
                  <td>
                    <Link
                      to={{ pathname: `/view_cust/${cust.cust_id}` }}
                      className="btn btn-warning"
                    >
                      View
                    </Link>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </div>
    );
  }
}

export default ViewCustomer
