import React, { Component } from 'react'
import { Modal, Button, Form, Alert } from 'react-bootstrap'
import "bootstrap/dist/css/bootstrap.css";
import axios from 'axios';
import config from "../config"

export class ModalView extends Component {
  constructor(props) {
    super(props)
  
    this.state = {
      sender_data: [],
      receiver_data: [],
      sender_id: "",
      receiver_id: "",
      amount: "",
      success: false
    }
  }
  componentDidMount() {
    axios
      .get(`${config.apiUrl}/get_cust_data.php`)
      .then(
        (res) => {
          this.setState({ sender_data: res.data });
        },
        (error) => {
          console.log(error);
        }
      );
  }
  handleSender = (e) => {
    this.setState({
      sender_id: e
    })
  }
  handleReceiver = () => {
    
  }
  render() {
    const isShow = this.props.show
    const isHide = this.props.hideClose
        return (
          <div>
            {/* Modal Code */}
            <Modal show={isShow} onHide={isHide}>
              <Modal.Header closeButton>
                <Modal.Title>Transfer Money</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <Form>
                  <Form.Group>
                    <Form.Label>Sender</Form.Label>
                    <Form.Control as="select" name="sender" onChange={this.handleSender.bind(this)} >
                      {this.state.sender_data.map((sd) => {
                        return (
                          <option value={sd.cust_id}>{sd.cust_name}</option>
                        );
                      })}
                    </Form.Control>
                  </Form.Group>

                  <Form.Group>
                    <Form.Label>Receiver</Form.Label>
                    <Form.Control
                      as="select"
                      name="receiver"
                      onChange={this.handleReceiver.bind(this)}
                    >
                      <option value>--Select Receiver--</option>
                      {this.state.receiver_data.map((rd) => {
                        return (
                          <option value={rd.cust_id}>{rd.cust_name}</option>
                        );
                      })}
                    </Form.Control>
                  </Form.Group>
                  <Form.Group controlId="amount">
                    <Form.Label>Transfer Amount</Form.Label>
                    <input
                      type="text"
                      name="amount"
                      placeholder="Enter Amount"
                      className="form-control"
                      onChange={this.handleAmount.bind(this)}
                      required
                    />
                  </Form.Group>
                </Form>
                {this.state.success ? (
                  <>
                    <Alert variant="success">
                      <p>Transaction Successfull.</p>
                      <p>Transaction details recorded successfully. </p>
                    </Alert>
                  </>
                ) : null}
              </Modal.Body>
              <Modal.Footer>
                {!this.state.success ? (
                  <>
                    <Button variant="danger" onClick={this.handleClose}>
                      Close
                    </Button>
                    <Button variant="warning" onClick={this.transferMoney}>
                      Tranfer Money
                    </Button>
                  </>
                ) : (
                  <Button variant="warning" onClick={this.handleReload}>
                    OK
                  </Button>
                )}
              </Modal.Footer>
            </Modal>
          </div>
        );
    }
}

export default ModalView;
